const HomePage = () => {
  const [activeSection, setActiveSection] = useState('summary');
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const mainRef = useRef(null);
  const { x: mouseX, y: mouseY } = useMousePosition();

  // Handle scroll and loading effects
  useEffect(() => {
    const handleScroll = () => {
      setShowScrollTop(window.pageYOffset > 300);
    };

    window.addEventListener('scroll', handleScroll);

    // Simulated loading
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 1500);

    return () => {
      window.removeEventListener('scroll', handleScroll);
      clearTimeout(timer);
    };
  }, []);

  // Handle keyboard navigation
  useEffect(() => {
    const handleKeyPress = (e) => {
      const sections = ['summary', 'film', 'commercial', 'experience', 'skills', 'personal', 'experimental'];
      if (e.key === 'ArrowDown' || e.key === 'ArrowUp') {
        const currentIndex = sections.indexOf(activeSection);
        const nextIndex = e.key === 'ArrowDown' 
          ? (currentIndex + 1) % sections.length
          : (currentIndex - 1 + sections.length) % sections.length;
        setActiveSection(sections[nextIndex]);
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [activeSection]);

  // Render loading screen
  if (isLoading) {
    return (
      <motion.div 
        className="fixed inset-0 bg-white z-50 flex flex-col items-center justify-center"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      >
        <motion.div
          className="w-24 h-24 border-4 border-gray-200 border-t-black rounded-full"
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
        />
        <motion.p
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5 }}
          className="mt-4 text-gray-600"
        >
          Loading Portfolio...
        </motion.p>
      </motion.div>
    );
  }

  // Render main content based on active section
  const renderContent = () => {
    switch (activeSection) {
      case 'summary':
        return renderSummary();
      case 'film':
        return renderFilmProjects();
      case 'commercial':
        return renderCommercialWork();
      case 'experience':
        return (
          <Section
            title="Professional Experience"
            subtitle="Career highlights and achievements"
          >
            <div className="space-y-8">
              {experienceData.film.concat(experienceData.commercial)
                .sort((a, b) => new Date(b.startDate) - new Date(a.startDate))
                .map((exp, index) => (
                  <ProjectCard
                    key={index}
                    {...exp}
                    className="border-l-4 border-gray-800"
                  />
                ))}
            </div>
          </Section>
        );
      case 'skills':
        return renderSkills();
      case 'personal':
        return renderPersonal();
      case 'experimental':
        return (
          <Section
            title="Experimental Work"
            subtitle="Pushing boundaries and exploring new techniques"
          >
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {experienceData.personal
                .filter(project => project.category === 'Experimental')
                .map((project, index) => (
                  <ProjectCard
                    key={index}
                    {...project}
                    variant="featured"
                  />
                ))}
            </div>
          </Section>
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col md:flex-row">
      {/* Sidebar */}
      <motion.nav
        variants={slideInRight}
        initial="initial"
        animate="animate"
        className="w-full md:w-64 bg-white border-b md:border-b-0 md:border-r 
          border-gray-200 p-6 md:h-screen md:sticky md:top-0"
      >
        <div className="flex flex-col h-full">
          {/* Profile Section */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            className="mb-6"
          >
            <div className="relative w-20 h-20 mb-4">
              <img
                src={profilePicture}
                alt="Profile"
                className="rounded-full object-cover w-full h-full"
              />
              <motion.div
                className="absolute -inset-0.5 rounded-full bg-gradient-to-r 
                  from-black to-gray-600 opacity-0"
                whileHover={{ opacity: 0.2 }}
              />
            </div>
            <h2 className="text-2xl font-bold text-gray-900">Martin Tomek</h2>
            <p className="text-gray-500">VFX Artist & Motion Designer</p>
          </motion.div>

          {/* Contact Info */}
          <motion.div
            variants={stagger}
            initial="initial"
            animate="animate"
            className="mb-6 space-y-2"
          >
            <ContactInfo icon={MapPin} text="Prague, Czech Republic" />
            <ContactInfo
              icon={Mail}
              text="martintomek.vfx@gmail.com"
              href="mailto:martintomek.vfx@gmail.com"
            />
            <ContactInfo
              icon={Phone}
              text="+420 774 630 132"
              href="tel:+420774630132"
            />
            <ContactInfo
              icon={Github}
              text="github.com/Themolx"
              href="https://github.com/Themolx"
            />
            <ContactInfo
              icon={Linkedin}
              text="LinkedIn Profile"
              href="https://www.linkedin.com/in/martin-tomek"
            />
          </motion.div>

          {/* Navigation */}
          <Navigation
            activeSection={activeSection}
            setActiveSection={setActiveSection}
          />

          {/* Footer */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5 }}
            className="mt-auto pt-6 border-t border-gray-200"
          >
            <p className="text-sm text-gray-500">Available for new opportunities</p>
            <p className="text-sm text-gray-500 mt-1">
              Willing to relocate to Canary Islands
            </p>
            <motion.a
              href="#"
              className="flex items-center mt-4 text-gray-600 hover:text-black 
                transition-colors group"
              whileHover={{ x: 5 }}
            >
              <Download size={16} className="mr-2 group-hover:scale-110 
                transition-transform" />
              <span className="text-sm">Download Resume</span>
            </motion.a>
          </motion.div>
        </div>
      </motion.nav>

      {/* Main Content */}
      <main
        ref={mainRef}
        className="flex-1 p-4 md:p-8 overflow-y-auto relative"
      >
        <AnimatePresence mode="wait">
          {renderContent()}
        </AnimatePresence>
      </main>

      {/* Mobile Menu Button */}
      <motion.button
        className="md:hidden fixed bottom-8 left-8 p-3 bg-black text-white 
          rounded-full shadow-lg hover:bg-gray-800 z-40"
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={() => setIsMobileMenuOpen(true)}
      >
        <Menu size={24} />
      </motion.button>

      {/* Mobile Navigation */}
      <MobileMenu
        isOpen={isMobileMenuOpen}
        setIsOpen={setIsMobileMenuOpen}
      >
        <Navigation
          activeSection={activeSection}
          setActiveSection={(section) => {
            setActiveSection(section);
            setIsMobileMenuOpen(false);
          }}
          isMobile
        />
      </MobileMenu>

      {/* Scroll to Top Button */}
      <AnimatePresence>
        {showScrollTop && (
          <motion.button
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
            className="fixed bottom-8 right-8 p-3 bg-black text-white rounded-full 
              shadow-lg hover:bg-gray-800 z-40"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <ChevronRight className="transform -rotate-90" size={24} />
          </motion.button>
        )}
      </AnimatePresence>

      {/* Mouse Cursor Effect */}
      <CursorHighlight />
    </div>
  );
};

export default HomePage;
