      <p className="text-gray-600 mb-4">{post.date}</p>
      <p>{post.excerpt}</p>
      <a href={`/blog/${post.id}`} className="text-purple-600 hover:text-purple-700 mt-4 inline-block">
        Read More â†’
      </a>
    </article>
  );
}

export default BlogPost;
