// Import necessary dependencies
import React, { useState, useEffect } from 'react';
import {
  MapPin,
  Mail,
  Phone,
  User,
  Github,
  Film,
  Code,
  Clapperboard,
  Briefcase,
  Download,
} from 'lucide-react';

// Component for project cards
const ProjectCard = ({ title, description, duration, media, mediaType, className = '' }) => {
  return (
    <div className={`bg-white rounded-xl shadow-lg p-6 space-y-4 ${className}`}>
      <div>
        <h3 className="text-2xl font-semibold text-gray-900">{title}</h3>
        {duration && <p className="text-sm text-gray-500 mt-1">{duration}</p>}
      </div>

      {media && mediaType === 'video' && (
        <div className="aspect-w-16 aspect-h-9 rounded-lg overflow-hidden">
          <iframe
            src={media}
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
            className="w-full h-full"
          ></iframe>
        </div>
      )}

      <p className="text-gray-700 whitespace-pre-line">{description}</p>
    </div>
  );
};

// Component for contact information items
const ContactItem = ({ icon: Icon, text, href }) => (
  <a
    href={href}
    className="flex items-center space-x-3 text-gray-600 hover:text-gray-900 transition-colors group"
    target={href ? '_blank' : undefined}
    rel={href ? 'noopener noreferrer' : undefined}
  >
    <Icon size={20} className="group-hover:text-gray-900" />
    <span className="text-lg">{text}</span>
  </a>
);

// Component for the profile section
const ProfileSection = () => (
  <div className="flex flex-col items-start space-y-6 mb-8">
    <div className="flex items-center space-x-6">
      <img
        src="/api/placeholder/96/96"
        alt="Martin Tomek"
        className="w-24 h-24 rounded-full object-cover"
      />
      <div>
        <h1 className="text-4xl font-bold text-gray-900">Martin Tomek</h1>
        <h2 className="text-xl text-gray-600 mt-2">Motion Designer & VFX Artist</h2>
      </div>
    </div>

    <div className="space-y-3 w-full">
      <ContactItem icon={MapPin} text="Prague, Czech Republic" />
      <ContactItem 
        icon={Mail} 
        text="martintomek.vfx@gmail.com" 
        href="mailto:martintomek.vfx@gmail.com"
      />
      <ContactItem 
        icon={Phone} 
        text="+420 774 630 132" 
        href="tel:+420774630132"
      />
      <ContactItem 
        icon={Github} 
        text="github.com/Themolx" 
        href="https://github.com/Themolx"
      />
    </div>
  </div>
);

// Main App component
const App = () => {
  const [activeSection, setActiveSection] = useState('summary');

  // Update page title on mount
  useEffect(() => {
    document.title = 'Martin Tomek - Portfolio';
  }, []);

  // Navigation sections
  const sections = [
    { id: 'summary', icon: User, label: 'Summary' },
    { id: 'compositing', icon: Film, label: 'Compositing' },
    { id: 'technical', icon: Code, label: 'Technical Direction' },
    { id: 'motion', icon: Briefcase, label: 'Motion Design' },
    { id: 'personal', icon: Clapperboard, label: 'Personal Projects' },
  ];

  // Render main content based on active section
  const renderContent = () => {
    switch (activeSection) {
      case 'summary':
        return (
          <div className="space-y-8">
            {/* Showreel Video */}
            <div className="aspect-w-16 aspect-h-9 rounded-xl overflow-hidden shadow-lg">
              <iframe
                src="https://player.vimeo.com/video/1016090207?h=your-hash&autoplay=1&loop=1&title=0&byline=0&portrait=0"
                allow="autoplay; fullscreen; picture-in-picture"
                allowFullScreen
                className="w-full h-full"
              ></iframe>
            </div>

            <ProfileSection />

            <div className="prose prose-lg max-w-none">
              <p className="text-xl leading-relaxed text-gray-700">
                I'm a passionate <span className="font-semibold">Mid-Level Compositor</span> and 
                <span className="font-semibold"> VFX Artist</span> specializing in creating seamless 
                visual effects and technical solutions. With extensive experience in Nuke compositing 
                and pipeline development, I combine technical expertise with creative vision to deliver 
                exceptional results.
              </p>
            </div>
          </div>
        );

      case 'compositing':
        return (
          <div className="space-y-8">
            <h2 className="text-3xl font-bold text-gray-900">Compositing Projects</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <ProjectCard
                title="Proud Princess"
                duration="PFX Studio | 2024"
                description="Lead compositor for feature film, handling complex VFX integration and ensuring seamless compositing of CG elements with live-action footage."
              />
              <ProjectCard
                title="Rosa & Dara"
                duration="Incognito Studio | 2021-Present"
                description="Key compositor for animated feature film, developing custom tools and managing technical aspects of the compositing pipeline."
              />
            </div>
          </div>
        );

      case 'technical':
        return (
          <div className="space-y-8">
            <h2 className="text-3xl font-bold text-gray-900">Technical Direction</h2>
            <div className="grid grid-cols-1 gap-6">
              <ProjectCard
                title="Pipeline Development"
                description="• Created automated workflows in Nuke and Python
• Developed custom tools for efficiency improvement
• Implemented new technical solutions for compositing pipeline"
              />
            </div>
          </div>
        );

      case 'motion':
        return (
          <div className="space-y-8">
            <h2 className="text-3xl font-bold text-gray-900">Motion Design</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <ProjectCard
                title="Commercial Projects"
                duration="Various Clients | 2021-Present"
                description="Motion graphics and animation for major brands including Panasonic, McDonald's, and T-Mobile."
              />
            </div>
          </div>
        );

      case 'personal':
        return (
          <div className="space-y-8">
            <h2 className="text-3xl font-bold text-gray-900">Personal Projects</h2>
            <ProjectCard
              title="Bachelor Movie"
              description="An experimental film combining 3D animation, AI-generated content, and analog techniques."
              media="https://www.youtube.com/embed/M6bm6yRKshA"
              mediaType="video"
              className="mb-6"
            />
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <ProjectCard
                title="Glitch Art & Experimental Animation"
                description="Exploring the intersection of analog and digital media through creative experimentation and AI integration."
              />
              <ProjectCard
                title="Technical Research"
                description="Developing new tools and techniques for VFX and motion design, with a focus on automation and efficiency."
              />
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <main className="container mx-auto px-4 py-8 max-w-7xl">
        {/* Navigation */}
        <nav className="mb-8">
          <div className="flex flex-wrap gap-4">
            {sections.map(({ id, icon: Icon, label }) => (
              <button
                key={id}
                onClick={() => setActiveSection(id)}
                className={`px-4 py-2 rounded-lg transition-colors flex items-center space-x-2 ${
                  activeSection === id
                    ? 'bg-black text-white'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
              >
                <Icon size={20} />
                <span>{label}</span>
              </button>
            ))}
          </div>
        </nav>

        {/* Content Area */}
