const ProjectCard = ({
  title,
  description,
  duration,
  details,
  technologies,
  media,
  mediaType,
  link,
  className = '',
  icon: Icon,
  variant = 'default',
  videoUrl,
  category,
  studio,
  alwaysExpanded = false
}) => {
  // Only allow expansion if it's not a commercial project
  const [isExpanded, setIsExpanded] = useState(
    alwaysExpanded ? true : (variant !== 'commercial' && false)
  );
  const [showGallery, setShowGallery] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [ref, isInView] = useInView({ threshold: 0.2 });
  const controls = useAnimation();

  useEffect(() => {
    if (isInView) {
      controls.start({ opacity: 1, y: 0 });
    }
  }, [isInView, controls]);

  const variants = {
    default: {
      background: 'bg-white',
      text: 'text-gray-900',
      hover: 'hover:shadow-lg hover:border-gray-300'
    },
    featured: {
      background: 'bg-gradient-to-br from-gray-900 to-gray-800',
      text: 'text-white',
      hover: 'hover:shadow-xl hover:shadow-gray-900/20'
    },
    commercial: {
      background: 'bg-white',
      text: 'text-gray-900',
      hover: 'hover:shadow-lg hover:border-gray-300'
    },
    outline: {
      background: 'bg-white',
      text: 'text-gray-900',
      hover: 'hover:border-black'
    }
  };

  const currentVariant = variants[variant];
  const canExpand = !alwaysExpanded && variant !== 'commercial';

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, y: 50 }}
      animate={controls}
      className={`
        rounded-xl overflow-hidden border transition-all duration-300
        ${currentVariant.background} ${currentVariant.hover} ${className}
        ${canExpand ? 'cursor-pointer' : ''}
      `}
      onClick={() => {
        if (canExpand) {
          setIsExpanded(!isExpanded);
        }
      }}
    >
      <div className="p-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            {Icon && (
              <div className={`p-2 rounded-lg ${
                variant === 'featured' ? 'bg-white/10' : 'bg-gray-100'
              }`}>
                <Icon 
                  size={24} 
                  className={variant === 'featured' ? 'text-white' : 'text-gray-600'} 
                />
              </div>
            )}
            <div>
              <h3 className={`text-xl font-semibold ${currentVariant.text}`}>
                {title}
              </h3>
              {duration && (
                <p className={`text-sm mt-1 ${
                  variant === 'featured' ? 'text-white/60' : 'text-gray-500'
                }`}>
                  {duration}
                </p>
              )}
              {studio && (
                <p className={`text-sm mt-0.5 ${
                  variant === 'featured' ? 'text-white/60' : 'text-gray-500'
                }`}>
                  {studio}
                </p>
              )}
            </div>
          </div>

          {/* Only show expand/collapse for non-commercial cards */}
          {canExpand && (
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              onClick={(e) => {
                e.stopPropagation();
                setIsExpanded(!isExpanded);
              }}
              className={variant === 'featured' ? 'text-white/80' : 'text-gray-500'}
            >
              {isExpanded ? (
                <Minimize size={20} />
              ) : (
                <Maximize size={20} />
              )}
            </motion.button>
          )}
        </div>

        {/* Description */}
        <div className={`mt-4 ${variant === 'featured' ? 'text-white/80' : 'text-gray-600'}`}>
          {description}
        </div>

        {/* Technologies - Always visible for commercial cards */}
        {technologies && (variant === 'commercial' || isExpanded) && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.2 }}
            className="flex flex-wrap gap-2 mt-4"
