import React from 'react';
import { Link } from 'react-router-dom';
import { blogPosts } from '../data/blogPosts';

function Blog() {
  return (
    <div className="container mx-auto px-4 py-16">
      <h1 className="text-4xl font-bold mb-12">Blog Posts</h1>
      <div className="space-y-8">
        {Object.values(blogPosts)
          .sort((a, b) => new Date(b.date) - new Date(a.date))
          .map(post => (
            <Link 
              key={post.slug}
              to={`/blog/${post.slug}`}
              className="block p-6 bg-[#111111] rounded-xl hover:bg-[#1a1a1a] transition-colors"
            >
              <time className="text-purple-400">{post.date}</time>
              <h2 className="text-2xl font-bold mt-2 mb-4">{post.title}</h2>
              <div className="flex gap-2">
                {post.tags.map(tag => (
                  <span 
                    key={tag} 
                    className="px-3 py-1 bg-[#222222] rounded-full text-sm"
                  >
                    {tag}
                  </span>
                ))}
              </div>
            </Link>
          ))}
      </div>
    </div>
  );
}

export default Blog;
