import React, { useState, useEffect, useRef } from "react";
import { motion, useAnimation, AnimatePresence } from "framer-motion";
import { useDrag } from "react-use-gesture";

export const BlobDemo = () => {
  const [isActive, setIsActive] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const blobRef = useRef(null);
  const controls = useAnimation();
  const audioContextRef = useRef(null);

  // Initialize Audio Context
  useEffect(() => {
    audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
    return () => {
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
    };
  }, []);

  // Synthesize pop sound
  const playPop = () => {
    if (!audioContextRef.current) return;

    const oscillator = audioContextRef.current.createOscillator();
    const gainNode = audioContextRef.current.createGain();

    oscillator.connect(gainNode);
    gainNode.connect(audioContextRef.current.destination);

    oscillator.frequency.setValueAtTime(400, audioContextRef.current.currentTime);
    oscillator.frequency.exponentialRampToValueAtTime(
      100,
      audioContextRef.current.currentTime + 0.1
    );

    gainNode.gain.setValueAtTime(0.3, audioContextRef.current.currentTime);
    gainNode.gain.exponentialRampToValueAtTime(
      0.01,
      audioContextRef.current.currentTime + 0.1
    );

    oscillator.start();
    oscillator.stop(audioContextRef.current.currentTime + 0.1);
  };

  // Synthesize hover sound
  const playHover = () => {
    if (!audioContextRef.current) return;

    const oscillator = audioContextRef.current.createOscillator();
    const gainNode = audioContextRef.current.createGain();

    oscillator.connect(gainNode);
    gainNode.connect(audioContextRef.current.destination);

    oscillator.frequency.setValueAtTime(200, audioContextRef.current.currentTime);
    oscillator.frequency.linearRampToValueAtTime(
      300,
      audioContextRef.current.currentTime + 0.1
    );

    gainNode.gain.setValueAtTime(0.2, audioContextRef.current.currentTime);
    gainNode.gain.linearRampToValueAtTime(
      0.01,
      audioContextRef.current.currentTime + 0.1
    );

    oscillator.start();
    oscillator.stop(audioContextRef.current.currentTime + 0.1);
  };

  // Handle draggable blobs
  const bind = useDrag(({ offset: [x, y] }) => {
    controls.start({
      x,
      y,
      transition: { type: "spring", stiffness: 300, damping: 30 }
    });
  });

  // Handle mouse move for particle trails
  const handleMouseMove = (e) => {
    const { clientX, clientY } = e;
    setMousePosition({ x: clientX, y: clientY });
  };

  // Dynamic background animation
  const backgroundVariants = {
    animate: {
      background: [
        "linear-gradient(45deg, #1D2B64, #F8CDDA)",
        "linear-gradient(135deg, #F8CDDA, #1D2B64)",
      ],
      transition: {
        duration: 10,
        repeat: Infinity,
        repeatType: "reverse"
      }
    }
  };

  return (
    <motion.div
      className="min-h-screen flex flex-wrap gap-8 justify-center items-center p-8 bg-gradient-to-br from-purple-900 via-blue-900 to-purple-900"
      variants={backgroundVariants}
      animate="animate"
      onMouseMove={handleMouseMove}
    >
      {/* Draggable Blob */}
      <motion.div
        {...bind()}
        className="w-64 h-64 relative cursor-grab shadow-lg rounded-full bg-gradient-to-r from-purple-500 to-pink-500"
        ref={blobRef}
        animate={controls}
        whileTap={{ cursor: "grabbing" }}
        onClick={() => {
          playPop();
          setIsActive(!isActive);
        }}
        whileHover={{ scale: 1.05, boxShadow: "0px 0px 20px rgba(255, 105, 180, 0.6)" }}
      >
        {/* Subtle Rotating Layer */}
        <motion.div
          className="absolute inset-0 rounded-full"
          style={{
            background: `radial-gradient(circle at 30% 30%, rgba(255, 255, 255, 0.3), transparent 70%)`
          }}
          animate={{
            rotate: [0, 360],
          }}
          transition={{
            duration: 15,
            repeat: Infinity,
            ease: "linear",
          }}
        />
        <span className="absolute inset-0 flex items-center justify-center text-white font-semibold text-lg z-10">
          {isActive ? "Active" : "Drag Me"}
        </span>
      </motion.div>

      {/* Interactive Particle Blob */}
      <motion.div
        className="w-64 h-64 bg-gradient-to-tr from-teal-400 to-blue-500 rounded-full relative overflow-hidden shadow-inner"
        onMouseMove={(e) => {
          const { currentTarget, clientX, clientY } = e;
          const { left, top } = currentTarget.getBoundingClientRect();
          setMousePosition({
            x: clientX - left,
            y: clientY - top,
          });
        }}
        whileHover={{ scale: 1.05, boxShadow: "0px 0px 15px rgba(0, 255, 255, 0.4)" }}
        onHoverStart={() => playHover()}
      >
        {/* Subtle Particle Trails */}
        <AnimatePresence>
          <motion.div
            className="absolute w-2 h-2 bg-white rounded-full"
            initial={{ opacity: 0, x: mousePosition.x, y: mousePosition.y }}
            animate={{
              opacity: [1, 0],
              x: mousePosition.x + (Math.random() - 0.5) * 50,
              y: mousePosition.y + (Math.random() - 0.5) * 50,
              scale: [1, 0.5],
            }}
            exit={{ opacity: 0 }}
            transition={{
              duration: 0.8,
              ease: "easeOut",
            }}
            key={`${mousePosition.x}-${mousePosition.y}-${Date.now()}`}
          />
        </AnimatePresence>
        <span className="absolute inset-0 flex items-center justify-center text-white font-semibold text-lg z-10">
          Hover Over Me
        </span>
      </motion.div>

      {/* Simple Morphing Button */}
      <motion.button
        className="relative w-64 h-64 bg-gradient-to-br from-green-400 to-yellow-500 rounded-full overflow-hidden cursor-pointer shadow-lg"
        {...bind()}
        animate={controls}
        whileTap={{ cursor: "grabbing" }}
        onClick={() => {
          playPop();
          setIsActive(!isActive);
        }}
        whileHover={{ scale: 1.05, boxShadow: "0px 0px 20px rgba(255, 255, 0, 0.5)" }}
      >
        {/* Morphing Shape */}
        <motion.div
          className="absolute inset-0 bg-white/20 rounded-full"
          animate={{
            borderRadius: isActive
              ? ["20% 80% 80% 20%", "80% 20% 20% 80%", "50% 50% 50% 50%"]
              : ["50% 50% 50% 50%", "20% 80% 80% 20%", "80% 20% 20% 80%"],
            rotate: isActive ? 360 : 0,
          }}
          transition={{
            duration: 5,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />

        {/* Text */}
        <motion.span
          className="absolute inset-0 flex items-center justify-center text-white font-bold text-lg z-10"
          animate={{
            scale: isActive ? [1, 1.2, 1] : 1,
          }}
          transition={{
            duration: 0.5,
            repeat: isActive ? Infinity : 0,
          }}
        >
          {isActive ? "Active" : "Unleash"}
        </motion.span>
      </motion.button>
    </motion.div>
  );
};
