// pages/index.js
import React, { useState, useRef, useEffect } from 'react';
import {
  Clapperboard,
  Code,
  Film,
  User,
  Mail,
  Phone,
  MapPin,
  Download,
  Briefcase,
  Github,
  Linkedin, // Added LinkedIn icon
} from 'lucide-react';

// ... [Glitch Images and Analog Images arrays remain the same]

// Your profile picture
const profilePicture =
  'https://avatars.githubusercontent.com/u/183303841?s=400&u=15ae10f7b65b15a10c0ddd7a9d12efbb608014f4&v=4';

/**
 * ProjectCard Component
 * Handles individual project display, hover effects, and media controls.
 */
const ProjectCard = ({
  // ... [Props remain the same]
}) => {
  // ... [Component logic remains the same]

  // Removed the placeholder text for Skills page hover details
  return (
    <div
      // ... [Container props remain the same]
    >
      {/* ... [Content remains the same] */}

      {/* Skills page hover details */}
      {/* Removed the placeholder text */}
    </div>
  );
};

/**
 * Main App Component
 */
const HomePage = () => {
  const [activeSection, setActiveSection] = useState('summary');

  // ... [UseEffect for updating the title remains the same]

  // Navigation items
  const sections = [
    { id: 'summary', icon: User, label: 'Summary' },
    { id: 'film', icon: Film, label: 'Film Projects' },
    { id: 'experience', icon: Briefcase, label: 'Experience' },
    { id: 'skills', icon: Code, label: 'Skills' },
    { id: 'personal', icon: Clapperboard, label: 'Personal Projects' },
  ];

  /**
   * NavItem Component
   * Handles individual navigation button styling and functionality.
   */
  const NavItem = ({ id, icon: Icon, label }) => (
    <button
      onClick={() => setActiveSection(id)}
      className={`w-full flex items-center px-4 py-3 rounded-lg transition-colors duration-500 ease-in-out ${
        activeSection === id
          ? 'bg-black text-white' // Removed hover effect on active item
          : 'text-gray-600 hover:text-black hover:bg-gray-100'
      }`}
    >
      <Icon size={20} className="min-w-[20px]" />
      <span className="ml-3 text-sm font-medium whitespace-nowrap">{label}</span>
    </button>
  );

  /**
   * ContactInfo Component
   * Displays contact information with icons.
   */
  const ContactInfo = ({ icon: Icon, text, href }) => (
    <a
      href={href}
      className="flex items-center text-gray-600 hover:text-black transition-colors duration-500 ease-in-out"
      target="_blank"
      rel="noopener noreferrer"
    >
      <Icon size={16} className="mr-2" />
      <span className="text-sm">{text}</span>
    </a>
  );

  /**
   * Renders content based on the active section.
   */
  const renderContent = () => {
    switch (activeSection) {
      // ... [Other cases remain the same]
      case 'experience':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">Professional Experience</h1>
            <div className="space-y-6">
              {/* ... [Other ProjectCards remain the same] */}
              <ProjectCard
                title="Nuke Compositor & VFX Supervisor"
                duration="Incognito Studio | August 2021 - Present"
                description="- Provided VFX services for various high-profile clients."
                moreInfo={
                  <div className="space-y-2">
                    <h4 className="font-semibold mb-2">Clients:</h4>
                    <ul className="list-disc pl-5 space-y-1 text-gray-700 leading-relaxed grid grid-cols-1 md:grid-cols-2 gap-2">
                      {/* Adjusted grid to be responsive */}
                      <li>Panasonic Japan</li>
                      <li>Moneta Bank</li>
                      <li>McDonald's</li>
                      <li>Ketoknoflik</li>
                      <li>PSS</li>
                      <li>T-Mobile</li>
                      <li>O2</li>
                      <li>7energy</li>
                      <li>Billa</li>
                      <li>Epet</li>
                    </ul>
                    <p className="text-gray-700 leading-relaxed">
                      VFX Supervisor for the Creditas project.
                    </p>
                    <p className="text-gray-700 leading-relaxed">
                      Working on "Rosa & Dara a jejich velké letní dobrodružství", a 3D feature film.
                    </p>
                  </div>
                }
                media={null}
                mediaType={null}
              />
              {/* ... [Other ProjectCards remain the same] */}
            </div>
          </div>
        );
      case 'skills':
        return (
          <div className="space-y-6">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">Skills</h1>
            <div className="grid grid-cols-1 gap-6">
              {/* Moved Programming above VJing and updated text */}
              <ProjectCard
                title="Software Proficiency"
                description={`• Nuke (3 years)
• After Effects (8 years)
• DaVinci Resolve
• Adobe Creative Suite`}
                moreInfo={
                  <p className="text-gray-700 leading-relaxed">
                    Experienced in creating complex visual effects and motion graphics using these
                    software tools.
                  </p>
                }
                media={null}
                mediaType={null}
              />
              <ProjectCard
                title="Pipeline & Workflow"
                description={`• OpenPype
• Ayon
• On-set VFX Supervision
• 2D and 3D Animation Integration`}
                moreInfo={
                  <p className="text-gray-700 leading-relaxed">
                    Skilled in optimizing production pipelines and integrating various animation
                    techniques.
                  </p>
                }
                media={null}
                mediaType={null}
              />
              {/* Programming moved up and more text added */}
              <ProjectCard
                title="Programming and Technical Direction"
                description={`• Python
• JavaScript
• Pipeline Scripting
• Custom Tool Development`}
                moreInfo={
                  <p className="text-gray-700 leading-relaxed">
                    Developed tailored scripts and tools for pipeline optimization in VFX production,
                    enhancing efficiency and productivity. Proficient in automating tasks, creating
                    custom plugins, and improving workflow efficiency through code.
                  </p>
                }
                media={null}
                mediaType={null}
              />
              {/* VJing remains but Resolume and TouchDesigner removed from Software Proficiency */}
              <ProjectCard
                title="VJing"
                description={`• Resolume Arena
• TouchDesigner`}
                moreInfo={
                  <p className="text-gray-700 leading-relaxed">
                    Experience in live visual performances and interactive installations using
                    Resolume Arena and TouchDesigner.
                  </p>
                }
                media={null}
                mediaType={null}
              />
              <ProjectCard
                title="AI"
                description={`• Understanding of AI and Machine Learning
• Huge Interest in New Technology`}
                moreInfo={
                  <p className="text-gray-700 leading-relaxed">
                    Exploring the integration of AI and machine learning into creative workflows.
                  </p>
                }
                media={null}
                mediaType={null}
              />
            </div>
          </div>
        );
      // ... [Other cases remain the same]
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col md:flex-row">
      {/* Sidebar */}
      <nav className="w-full md:w-64 bg-white border-b md:border-b-0 md:border-r border-gray-200 p-6">
        <div className="flex flex-col md:h-full">
          {/* Profile Picture */}
          {/* ... [Profile picture section remains the same] */}

          {/* Contact Info */}
          <div className="mb-6 space-y-2">
            <ContactInfo icon={MapPin} text="Prague, Czech Republic" />
            <ContactInfo
              icon={Mail}
              text="martintomek.vfx@gmail.com"
              href="mailto:martintomek.vfx@gmail.com"
            />
            <ContactInfo
              icon={Phone}
              text="+420 774 630 132"
              href="tel:+420774630132"
            />
            <ContactInfo
              icon={Github}
              text="github.com/Themolx"
              href="https://github.com/Themolx"
            />
            {/* Added LinkedIn ContactInfo */}
            <ContactInfo
              icon={Linkedin}
              text="linkedin.com/in/martin-tomek"
              href="https://www.linkedin.com/in/martin-tomek-1762b8196/"
            />
          </div>

          {/* Navigation */}
          {/* ... [Navigation remains the same] */}

          {/* Footer */}
          {/* ... [Footer remains the same] */}
        </div>
      </nav>

      {/* Main Content */}
      <main className="flex-1 p-4 md:p-8 overflow-y-auto pb-[600px]">
        {renderContent()}
      </main>
    </div>
  );
};

export default HomePage;
