import React from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import DownloadButton from '../components/blog/DownloadButton';

function BlogPost() {
  const { slug } = useParams();

  const blogPosts = {
    'nuke-advanced-grab-tool': {
      title: "ðŸš€ From Frustration to Innovation: The Birth of Nuke's Advanced Grab Tool",
      date: "November 9, 2024",
      author: "Martin Tomek",
      tags: ["Nuke", "Python", "Workflow", "Technical Guide"],
      heroImage: "/images/grab-tool-hero.gif",
      content: [
        {
          type: 'story-intro',
          content: "Picture this: You're deep into a complex comp, your Node Graph looks like a spider's web after too much coffee, and you're playing that familiar game of 'drag and pray' with your nodes. We've all been there. That's exactly why I created the Advanced Grab Tool - because life's too short for messy node trees! ðŸ•¸ï¸"
        },
        {
          type: 'quick-demo',
          content: {
            gif: "/images/quick-demo.gif",
            caption: "3 seconds to reorganize what used to take 3 minutes"
          }
        },
        {
          type: 'feature-highlight',
          content: {
            title: "The Magic Shortcuts âœ¨",
            features: [
              {
                key: "E",
                description: "The 'Everything Stays Where It Should' button",
                gif: "/images/standard-grab.gif"
              },
              {
                key: "Ctrl+E",
                description: "The 'Bring Your Friends Along' button",
                gif: "/images/tree-grab.gif"
              },
              {
                key: "Alt+Ctrl+E",
                description: "The 'Family Reunion' button",
                gif: "/images/full-grab.gif"
              }
            ]
          }
        },
        {
          type: 'ai-journey',
          content: {
            title: "When AI Meets Nuke: A Love Story ðŸ’˜",
            story: "Here's where it gets interesting. I didn't just sit down and code this tool - I paired up with Claude AI, and we had some pretty entertaining conversations:",
            conversations: [
              {
                me: "Hey Claude, how do I make nodes move like in Blender?",
                claude: "Let me help you with that! First, we'll need to hijack Nuke's event system...",
                result: "And that's how our node-moving adventure began!"
              },
              {
                me: "Claude, my nodes are dancing around like they're at a rave",
                claude: "Sounds fun, but maybe we should add some zoom level handling...",
                result: "And just like that, our nodes learned to behave!"
              }
            ]
          }
        },
        {
          type: 'github-story',
          content: {
            title: "The GitHub Chronicles ðŸ“š",
            commits: [
              {
                date: "Oct 2",
                message: "First commit: It's alive! ðŸŽ‰",
                story: "Basic grab functionality - nodes finally following cursor without throwing tantrums"
              },
              {
                date: "Oct 3",
                message: "Version 3.2: Now with 100% more awesome âš¡",
                story: "Added tree selection - because nodes should stick together like a family"
              },
              {
                date: "Oct 6",
                message: "Version 3.8: The 'Why Didn't I Think of This Before' update ðŸ¤”",
                story: "Added undo functionality because we all make mistakes (especially before coffee)"
              }
            ]
          }
        },
        {
          type: 'try-it-yourself',
          content: {
            title: "Your Turn to Play! ðŸŽ®",
            steps: [
              "1. Fire up Nuke (coffee optional but recommended)",
              "2. Copy this magical code into your Script Editor",
              "3. Watch your nodes obey your every command",
              "4. Feel like a Node Graph wizard ðŸ§™â€â™‚ï¸"
            ],
            code: `# The secret sauce
import nuke
# More code that makes the magic happen...`
          }
        },
        {
          type: 'community',
          content: {
            title: "Join the Node Movement! ðŸŒŸ",
            links: [
              {
                platform: "Nukepedia",
                url: "https://www.nukepedia.com/python/nodegraph/nuke-grab-tool",
                description: "Where the cool kids get their tools"
              },
              {
                platform: "GitHub",
                url: "https://github.com/Themolx/NukeScripts",
                description: "Watch the tool evolve in real-time"
              }
            ]
          }
        }
      ]
    }
  };

  const post = blogPosts[slug];

  const renderContent = (content) => {
    switch (content.type) {
      case 'paragraph':
        return <p className="text-gray-300 leading-relaxed mb-6">{content.content}</p>;

      case 'heading':
        return <h2 className="text-2xl font-bold text-white mt-12 mb-6">{content.content}</h2>;

      case 'download-section':
        return (
          <div className="flex flex-col sm:flex-row gap-4 my-8">
            <DownloadButton 
              url={content.content.nukepedia}
              text="Download from Nukepedia"
            />
            <DownloadButton 
              url={content.content.github}
              text="View on GitHub"
            />
          </div>
        );

      case 'video-demo':
        return (
          <div className="aspect-video w-full rounded-lg overflow-hidden my-8">
            <iframe
              src={`https://www.youtube.com/embed/${content.videoId}`}
              className="w-full h-full"
              allowFullScreen
            />
          </div>
        );

      case 'steps':
        return (
          <div className="space-y-6 my-8">
            {content.content.map((step, index) => (
              <div key={index} className="bg-white/5 rounded-lg p-6">
                <h3 className="text-xl font-semibold text-purple-400 mb-3">
                  {step.title}
                </h3>
                <p className="text-gray-300 whitespace-pre-line">
                  {step.description}
                </p>
              </div>
            ))}
          </div>
        );

      case 'code-example':
        return (
          <div className="my-8">
            <h3 className="text-xl font-semibold mb-4">{content.content.title}</h3>
            <pre className="bg-black/50 p-4 rounded-lg overflow-x-auto">
              <code className="text-sm text-gray-300">
                {content.content.code}
              </code>
            </pre>
          </div>
        );

      case 'try-yourself':
        return (
          <div className="bg-purple-900/20 rounded-lg p-6 my-8">
            <h3 className="text-xl font-semibold mb-4">{content.content.title}</h3>
            <ul className="list-decimal list-inside space-y-2 mb-6">
              {content.content.steps.map((step, index) => (
                <li key={index} className="text-gray-300">{step}</li>
              ))}
            </ul>
            <pre className="bg-black/50 p-4 rounded-lg overflow-x-auto">
              <code className="text-sm text-gray-300">
                {content.content.sampleCode}
              </code>
            </pre>
          </div>
        );

      default:
        return null;
    }
  };

  if (!post) {
    return <div className="container mx-auto px-4 py-24">Post not found</div>;
  }

  return (
    <motion.article
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="max-w-4xl mx-auto px-4 py-24"
    >
      <div className="mb-8">
        <span className="text-purple-400">{post.date}</span>
        <h1 className="text-4xl font-bold mt-2 mb-4">{post.title}</h1>
        <div className="flex items-center gap-2 text-gray-400">
          <span>By {post.author}</span>
          <span>â€¢</span>
          <div className="flex gap-2">
            {post.tags.map(tag => (
              <span key={tag} className="text-sm bg-white/10 px-3 py-1 rounded-full">
                {tag}
              </span>
            ))}
          </div>
        </div>
      </div>

      <div className="prose prose-invert max-w-none">
        {post.content.map((section, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            {renderContent(section)}
          </motion.div>
        ))}
      </div>
    </motion.article>
  );
}

export default BlogPost;
