import React, { useState } from 'react';
import { Clapperboard, Code, Film, User } from 'lucide-react';

function App() {
  const [activeSection, setActiveSection] = useState('compositing');

  const NavItem = ({ id, icon, label }) => (
    <button
      onClick={() => setActiveSection(id)}
      className={`nav-item ${activeSection === id ? 'nav-item-active' : 'nav-item-inactive'}`}
    >
      {icon}
      <span className="font-medium">{label}</span>
    </button>
  );

  const ProjectCard = ({ title, description }) => (
    <div className="section-card">
      <h2 className="text-xl font-semibold mb-2">{title}</h2>
      <p className="text-white/60">{description}</p>
    </div>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'compositing':
        return (
          <div>
            <h1 className="text-4xl font-bold mb-6">Compositing</h1>
            <p className="text-xl text-white/80 mb-12 max-w-2xl">
              Specialized in creating seamless visual effects and integrations using Nuke. 
              Extensive experience in film production and advertising projects.
            </p>
            <div className="grid gap-6">
              <ProjectCard 
                title="Project Alpha" 
                description="Visual effects for major film production" 
              />
              <ProjectCard 
                title="Commercial Work" 
                description="Commercial projects and advertising" 
              />
              <ProjectCard 
                title="Film Integration" 
                description="Seamless visual effects integration" 
              />
            </div>
          </div>
        );
      // ... other sections similar to compositing
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-black text-white">
      <nav className="fixed top-0 left-0 w-20 h-screen bg-black/50 backdrop-blur-sm border-r border-white/10">
        <div className="flex flex-col items-center h-full py-8">
          <div className="text-2xl font-bold mb-12">MT</div>
          <div className="flex flex-col gap-4">
            <NavItem id="compositing" icon={<Clapperboard size={20} />} label="Compositing" />
            <NavItem id="technical" icon={<Code size={20} />} label="Technical" />
            <NavItem id="motion" icon={<Film size={20} />} label="Motion" />
            <NavItem id="personal" icon={<User size={20} />} label="Personal" />
          </div>
        </div>
      </nav>
      <main className="ml-20 p-8">
        {renderContent()}
      </main>
    </div>
  );
}

export default App;
