import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { getProductThumb, placeholderVideo } from '../../utils/placeholders';
import { Link } from 'react-router-dom';
import GlitchButton from '../common/GlitchButton';

function ProductCard({ product }) {
  return (
    <motion.div
      className="group relative rounded-2xl overflow-hidden bg-[#111111]"
      whileHover={{ y: -5 }}
    >
      <Link to={product.link} className="block">
        <div className="aspect-video relative overflow-hidden">
          <img 
            src={product.thumbnail}
            alt={product.title}
            className="w-full h-full object-cover transition-transform 
                     group-hover:scale-105 duration-500"
          />
          {product.salePrice && (
            <div className="absolute top-4 right-4 bg-purple-600 text-white 
                          px-3 py-1 rounded-full text-sm font-medium">
              SAVE {Math.round((1 - product.salePrice/product.price) * 100)}%
            </div>
          )}
        </div>

        <div className="p-6">
          <h3 className="text-xl font-medium mb-2">{product.title}</h3>
          <p className="text-gray-400 text-sm mb-4">{product.description}</p>

          <div className="flex flex-wrap gap-2 mb-4">
            {product.software.map(sw => (
              <span key={sw} className="px-3 py-1 bg-white/5 rounded-full text-sm">
                {sw}
              </span>
            ))}
          </div>

          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              {product.salePrice ? (
                <>
                  <span className="text-2xl font-bold">${product.salePrice}</span>
                  <span className="text-gray-400 line-through">${product.price}</span>
                </>
              ) : (
                <span className="text-2xl font-bold">${product.price}</span>
              )}
            </div>
            <GlitchButton className="px-6 py-2 bg-white text-black rounded-full 
                                   font-medium hover:bg-gray-100">
              View Details
            </GlitchButton>
          </div>
        </div>
      </Link>
    </motion.div>
  );
}

export default ProductCard;
